<script>

async function fetchJson() {
  const response = await fetch("{{asset("assets/js/countries_list.json")}}"); // Replace with the actual path to your JSON file
  const data = await response.json();
  return data.countries;
}

async function getUserLocation() {
  try {
    const response = await fetch('https://ipapi.co/json/'); // IP geolocation API
    const location = await response.json();
    return location.country_name;
  } catch (error) {
    console.error('Error fetching user location:', error);
    return null;
  }
}

async function updateTargetLocation() {

  const countries = await fetchJson();
  const userCountry = await getUserLocation();

  if (userCountry) {
    const countryData = countries.find(country => country.country === userCountry);
    if (countryData) {
      const target_loc = document.getElementById('target-location');
      if (target_loc){

      target_loc.textContent = countryData.target_capital;
      }
    } 
  }
}
var userCountryLoc = getUserLocation();
document.addEventListener('DOMContentLoaded', updateTargetLocation);

</script>
